export default function useAutomaticScroller<ItemT>({ highlightedIndex, sections, groupBy, }: {
    highlightedIndex: number;
    sections: {
        title: string;
        data: ItemT[];
    }[];
    groupBy?: (option: ItemT) => string;
}): {
    onViewableItemsChanged: (p: any) => void;
    ref: any;
};
